import React, { useContext, useEffect, useState } from 'react'
import './CountryPage.css'
import { MyContext } from '../../MyContext'


const CountryPage = ({id}) => {
    const countries = useContext(MyContext)
    const [countryInfo, setcountryInfo] = useState(countries.countriesData.find((country,i) => i == id ? country : null))
    const [langs, setLangs] = useState(null)

    
    const getLanguages = () => {
        let lgsGroup = []
        for(let i in countryInfo.languages) {
            lgsGroup.push(countryInfo.languages[i])
        }
        return lgsGroup
    }

    useEffect(() => {
      setLangs(getLanguages())
    
      return () => {
        
      }
    }, [])
    console.log(langs)
  return (
    <section className='countryPage--section'>
        <div className='flag--container'>
            <img src={countryInfo.flags.png} />
        </div>
        <h2></h2>
        <div className='country--info'>
            <ul>
                <li>
                    <h3>Population</h3>
                    <h4>{countryInfo.population}</h4>
                </li>
                <li>
                    <h3>Capital</h3>
                    <h4>
                        {countryInfo.capital.map(capital => {
                            return (
                                <span>{capital}</span>
                            )
                        })}
                    </h4>
                </li>
                <li>
                    <h3>Language</h3>
                    <ul>
                        {/* {countryInfo.languages.map(language => {
                            return (
                                <li>
                                    <h4>{language}</h4>
                                </li>
                            )
                         })} */}
                    </ul>
                </li>
            </ul>
        </div>

        <div className='borders--container'>
                <h3>Borders</h3>
                <ul className='borders--list'>
                    {countryInfo.borders.map(border => {
                        return (
                            <li>
                                <a>{border}</a>
                            </li>
                        )
                    })}
                </ul>
        </div>

    </section>
  )
}

export default CountryPage
