import React, { useContext, useState } from 'react'
import './countriesSection.css'
import { MyContext } from '../../MyContext'

const CardCountry = ({countryName, flag, handleClickCard,id }) => {
 
    return (
      <div className='cardCountry' onClick={() => handleClickCard(id)}>
        <div className={countryName == "Nepal" ? "nepal--style flag--container" : "flag--container"}>
            <img src={flag}/>
        </div> 
        <h3>{countryName}</h3>
      </div>
    )
}


const CountriesSection = ({handleClickCard,filteredCountry, chosenContinent}) => {
  debugger
    let countries = useContext(MyContext)
    if (filteredCountry.length > 0) {
      countries = countries.countriesData.filter((country,id) => {
        if (country.name.common.toLowerCase().includes(filteredCountry.toLowerCase())){
          return country
        }
      })
    } else if (chosenContinent !== "Any") {
      
    }


    
  return (
    <section className='countries--section'>
        <h2>Click on the Card Country for more info</h2>
        <div className='countries--list'>
            {(chosenContinent !== "Any" && filteredCountry.length == 0) && countries.countriesData.map((country,i) => {
              if (country.continents[0] == chosenContinent) {
                return (
                  <CardCountry 
                      countryName={country.name.common} 
                      flag={country.flags.png} 
                      key={country.name.common}
                      id = {i}
                      handleClickCard = {handleClickCard}
                  />
              ) 
              }
            })}

            {filteredCountry.length == 0 && countries.countriesData.map((country,i) => {
                return (
                    <CardCountry 
                        countryName={country.name.common} 
                        flag={country.flags.png} 
                        key={country.name.common}
                        id = {i}
                        handleClickCard = {handleClickCard}
                    />
                )
            })}

            {filteredCountry.length >= 1 && countries.countriesData.map((country,i) => {
              if (country.name.common.toLowerCase().includes(filteredCountry.toLowerCase())) {
                return (
                    <CardCountry 
                        countryName={country.name.common} 
                        flag={country.flags.png} 
                        key={country.name.common}
                        id = {i}
                        handleClickCard = {handleClickCard}
                    />
                )
              }
              
            })}
        </div>
    </section>
    
  )
}

export default CountriesSection
