import React, { useContext, useState } from 'react'
import './countriesSection.css'
import { MyContext } from '../../MyContext'

const CardCountry = ({countryName, flag,capital,population, language, borders, handleClickCard}) => {
   
    return (
      <div className='cardCountry' onClick={() => handleClickCard(countryName, capital, language, borders, flag)}>
        <div className={countryName == "Nepal" ? "nepal--style flag--container" : "flag--container"}>
            <img src={flag}/>
        </div> 
        <h3>{countryName}</h3>
      </div>
    )
}


const CountriesSection = ({handleClickCard}) => {
    const countries = useContext(MyContext)

  return (
    <section className='countries--section'>
        <h2>Select a Country for more info</h2>
        <div className='countries--list'>
            {countries.countriesData.map(country => {
                return (
                    <CardCountry 
                        countryName={country.name.common} 
                        flag={country.flags.png} 
                        key={country.name.common}
                        handleClickCard = {handleClickCard}
                        capital = {country.capital}
                        language = {country.languages}
                        population={country.population}
                        borders = {country.borders}
                    />
                )
            })}
        </div>
    </section>
    
  )
}

export default CountriesSection
