import React, { useContext, useEffect, useState } from 'react'
import './CountryPage.css'
import { MyContext } from '../../MyContext'


const Border = ({countryBorder}) => {
    return (
        <li>
            <a>
                {countryBorder}
            </a>   
        </li>
    )
}


const CountryPage = ({id}) => {
    const countries = useContext(MyContext)
    const [countryInfo, setcountryInfo] = useState(null)

    useEffect(() => {
      const getCountry = () => {
        let c = countries.map((country,i) => {
            if (i == id) return country
        })

        return c
      }

      setcountryInfo(getCountry())
    
      return () => {
        setcountryInfo(null)
      }
    }, [])
    

    console.log(countries)
  return (
    <section className='countryPage--section'>
        <div className='flag--container'>
            <img />
        </div>
        <h2></h2>
        <div className='country--info'>
            <ul>
                <li>
                    <h3></h3>
                    <h4></h4>
                </li>
                <li>
                    <h3></h3>
                    <h4></h4>
                </li>
                <li>
                    <h3></h3>
                    <h4></h4>
                </li>
            </ul>
        </div>

        <div className='borders--container'>
                <h3></h3>
                <ul className='borders--list'>
                    <Border/>
                </ul>
        </div>

    </section>
  )
}

export default CountryPage
