import React, { useState } from 'react'
import './initialPage.css'
import worldLogo from '../../worldLogo.png'
import FilterSections from '../../components/filterForm'
import CountriesSection from '../../components/countriesSection/countriesSection'
import CountryPage from '../CountryPage/CountryPage'

const HomePage = () => {

  const [countrySelected, setCountrySelected] = useState(false)
  const [countryInfo, setCountryInfo] = useState(null)
  
  const handleClickCard = (countryName, capital, language, borders, flag) => {
    if (!countrySelected){
      setCountryInfo([countryName, capital, language, borders, flag])
      setCountrySelected(true)
    } else setCountrySelected(false)
    
  }

  return (
    
    <main>
      {countrySelected && <CountryPage
        countryName = {countryInfo[0]}
        capital = {countryInfo[1]}
        language = {countryInfo[2]}
        borders = {countryInfo[3]}
        flag = {countryInfo[4]}
      />}
      <section className='heroPage'>
        <img src={worldLogo}/>
        <div className='home--title'>
          <h1>World Info</h1>
          <h2>Made By Ruan Mesquita</h2>
        </div> 
      </section>
      <FilterSections/>
      <CountriesSection handleClickCard = {handleClickCard}/>
    </main>
  )
}

export default HomePage
