import React, { useContext } from 'react'
import './countriesSection.css'
import { MyContext } from '../../MyContext'

const CardCountry = ({countryName, flag}) => {

    return (
      <div className='cardCountry'>
        <img src={flag}/>
        <h3>{countryName}</h3>
      </div>
    )
}


const CountriesSection = () => {
    const countries = useContext(MyContext)

  return (
    <section className='countries--section'>
        {countries.countriesData.map(country => {
            return (
                <CardCountry countryName={country.name.common} flag={country.flags.png} key={country.name.common}/>
            )
        })}
    </section>
  )
}

export default CountriesSection
