import React, { useContext, useState } from 'react'
import './CountryPage.css'
import { MyContext } from '../../MyContext'
import { IoMdClose } from "react-icons/io";


const CountryPage = ({id, handleClickCard}) => {
    const countries = useContext(MyContext)
    const [countryInfo, setcountryInfo] = useState(countries.countriesData.find((country,i) => i == id ? country : null))

    
    const getLanguages = () => {
        let lgsGroup = []
        for(let i in countryInfo.languages) {
            lgsGroup.push(countryInfo.languages[i])
        }
        return lgsGroup
    }

    const langs = getLanguages()

    console.log(countryInfo.cca3)
  return (
    <section className='countryPage--section'>
        <button type='button' onClick={handleClickCard} className='close--Btn'>
                <IoMdClose/>
        </button>
        <div>
            
            <div className='flag--img'>
                <img src={countryInfo.flags.png} />
            </div>
            <h2>{countryInfo.name.common}</h2>
        </div>
        
        <div className='country--info'>
            <ul className='country--info--list'>
                <li>
                    <h3>Population</h3>
                    <h4>{Intl.NumberFormat('en-US').format(countryInfo.population)}</h4>
                </li>
                <li>
                    <h3>Capital</h3>
                    <div>
                        {countryInfo.capital.map((capital) => {
                        
                            return (
                                <h4>{capital} </h4>
                            )
                        })}
                    </div>
                </li>
                <li>
                    <h3>Language</h3>
                    <ul>
                        {langs.map(language => {
                            return (
                                <li>
                                    <h4>{language}</h4>
                                </li>
                            )
                         })}
                    </ul>
                </li>
                <li>
                    <h3>Continent</h3>
                    <h4>{countryInfo.continents[0]}</h4>
                </li>
            </ul>
        </div>

        <div className='borders--container'>
                <h3>Borders</h3>
                <ul className='borders--list'>
                    {countryInfo.borders !== undefined &&countryInfo.borders.map(border => {
                        debugger
                        return (
                            <li>
                                <button>{countries.countriesData.find(country => country.cca3 === border ? country.flags.png : null)}</button>
                            </li>
                        )
                    })}
                    {countryInfo.borders == undefined && <h4>None</h4>}
                </ul>
        </div>

    </section>
  )
}

export default CountryPage
